// Parse do nível
let nivel = parseInt(msg.payload);
if (isNaN(nivel)) return null;

let status = "";
let cor = "blue";

if (nivel <= 1500) {
    status = "Baixo";
    cor = "red";
} else if (nivel <= 3000) {
    status = "Médio";
    cor = "orange";
} else {
    status = "Alto";
    cor = "green";
}

return [
    { payload: nivel, topic: "nivel" }, 
    { payload: status }, 
    { payload: { background: cor, label: `Status: ${status}` } }, // Template
    { payload: "" }
];
